import { useState } from "react";
import Button from "react-bootstrap/Button";
import Modal from "react-bootstrap/Modal";
import Form from "react-bootstrap/Form";
import Input from "./Input";

function Edit({ name, title, index, handleEditName }) {
  const [show, setShow] = useState(false);
  const [newText, setNewText] = useState(itemToChange);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  const handleChange = (e) => {
    console.log("input : " + e.target.value);
    setNewText(e.target.value);
    console.log("newText : " + newText);
    itemToChange = newText;
  };

  return (
    <div>
      <Button style={{ width: "5rem" }} variant="primary" onClick={handleShow}>
        Edit
      </Button>

      <Modal
        show={show}
        onHide={handleClose}
        backdrop="static"
        keyboard={false}
      >
        <Modal.Header closeButton>
          <Modal.Title>Edit Employee</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <div style={{ margin: "0 0 10px 0" }}>
            <Form.Label htmlFor="inputPassword5">{label}</Form.Label>
            <Form.Control
              value={newText}
              onChange={handleChange}
              type="text"
              id="input"
            />
          </div>

          <Input label="Name" itemToChange={name}></Input>

          <Input label="Title" itemToChange={title}></Input>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Close
          </Button>
          <Button
            variant="primary"
            onClick={() => {
              handleEditName(index, name);
            }}
          >
            Confirm
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
}

export default Edit;
