import { useState } from "react";
import Button from "react-bootstrap/Button";
import Modal from "react-bootstrap/Modal";
import Form from "react-bootstrap/Form";
import styled from "styled-components";
import axios from "axios";

const StyledButton = styled(Button)`
  width: 12rem;
  height: 4rem;
`;

const DivWithMargin = styled.div`
  margin-bottom: 1rem;
`;

const WarningText = styled(Form.Text)`
  color: red;
`;

function Add({ handleAdd }) {
  const [show, setShow] = useState(false);
  const [isNameInvalid, setIsNameInvalid] = useState(false);
  const [isTitleInvalid, setIsTitleInvalid] = useState(false);

  const [newName, setNewName] = useState("");
  const [newTitle, setNewTitle] = useState("");
  const [newPicture, setNewPicture] = useState("");

  const handleShow = () => setShow(true);
  const handleChangeName = (e) => setNewName(e.target.value);
  const handleChangeTitle = (e) => setNewTitle(e.target.value);
  const handleChangePicture = (e) => setNewPicture(e.target.value);

  const handleClose = () => {
    setNewName("");
    setNewTitle("");
    setNewPicture("");
    setShow(false);
    setIsNameInvalid(false);
    setIsTitleInvalid(false);
  };

  const handleClick = () => {
    if (newName === "") {
      setIsNameInvalid(true);
    } else {
      setIsNameInvalid(false);
    }

    if (newTitle === "") {
      setIsTitleInvalid(true);
    } else {
      setIsTitleInvalid(false);
    }

    if (newTitle === "" || newName === "") {
      return;
    }

    const capitalizedFirst =
      newName.charAt(0).toUpperCase + newName.substring(1);
    console.log(capitalizedFirst);

    if (newPicture === "") {
      const noPicUrl =
        "https://upload.wikimedia.org/wikipedia/commons/thumb/b/bc/Unknown_person.jpg/925px-Unknown_person.jpg";
      handleAdd(newName, newTitle, noPicUrl);
      handleClose();
      return;
    }

    handleAdd(newName, newTitle, newPicture);
    handleClose();
  };

  const sendAddRequest = () => {
    const newEmployee = {
      name: newName,
      title: newTitle,
      img: newPicture,
    };
    axios
      .post("/api/employees", newEmployee)
      .then((response) => {
        console.log("Employee has been added:", response.data);
      })
      .catch((error) => {
        console.error("Error:", error);
      });
  };

  return (
    <>
      <StyledButton variant="primary" onClick={handleShow}>
        + Add New Employee
      </StyledButton>

      <Modal
        show={show}
        onHide={handleClose}
        backdrop="static"
        keyboard={false}
      >
        <Modal.Header>
          <Modal.Title>Add Employee</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <DivWithMargin>
            <Form.Label htmlFor="name">Name</Form.Label>
            <Form.Control
              style={{
                borderColor: isNameInvalid ? "red" : "",
              }}
              value={newName}
              onChange={handleChangeName}
              type="text"
              id="input"
            />
            {isNameInvalid ? <WarningText>Required</WarningText> : null}
          </DivWithMargin>

          <DivWithMargin>
            <Form.Label htmlFor="title">Title</Form.Label>
            <Form.Control
              style={{ borderColor: isTitleInvalid ? "red" : "" }}
              value={newTitle}
              onChange={handleChangeTitle}
              type="text"
              id="input"
            />
            {isTitleInvalid ? <WarningText>Required</WarningText> : null}
          </DivWithMargin>

          <DivWithMargin>
            <Form.Label htmlFor="inputPassword5">
              Image URL (Optional)
            </Form.Label>
            <Form.Control
              value={newPicture}
              onChange={handleChangePicture}
              type="text"
              id="input"
            />
          </DivWithMargin>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Close
          </Button>
          <Button variant="primary" onClick={handleClick}>
            Add
          </Button>
        </Modal.Footer>
      </Modal>
    </>
  );
}

export default Add;
