import React from "react";
import Profile from "./components/Profile";
import { useState } from "react";

function App() {
  const people = [
    {
      name: "Alice",
      title: "Software Developer",
      img: "https://images.pexels.com/photos/2169434/pexels-photo-2169434.jpeg",
      index: 0,
    },
    {
      name: "Bob",
      title: "Graphic Designer",
      img: "https://images.pexels.com/photos/1222271/pexels-photo-1222271.jpeg",
      index: 1,
    },
    {
      name: "Frank",
      title: "Data Analyst",
      img: "https://images.pexels.com/photos/614810/pexels-photo-614810.jpeg",
      index: 2,
    },
    {
      name: "Susan",
      title: "Product Manager",
      img: "https://images.pexels.com/photos/1239291/pexels-photo-1239291.jpeg",
      index: 3,
    },
    {
      name: "Eva",
      title: "Web Developer",
      img: "https://images.pexels.com/photos/415829/pexels-photo-415829.jpeg",
      index: 4,
    },
    {
      name: "Charlie",
      title: "UI/UX Designer",
      img: "https://images.pexels.com/photos/773371/pexels-photo-773371.jpeg",
      index: 5,
    },
    {
      name: "Grace",
      title: "Marketing Specialist",
      img: "https://images.pexels.com/photos/2104252/pexels-photo-2104252.jpeg",
      index: 6,
    },
    {
      name: "Henry",
      title: "System Administrator",
      img: "https://images.pexels.com/photos/3799786/pexels-photo-3799786.jpeg",
      index: 7,
    },
    {
      name: "Irene",
      title: "Content Writer",
      img: "https://images.pexels.com/photos/2083751/pexels-photo-2083751.jpeg",
      index: 8,
    },
    {
      name: "Jack",
      title: "Business Analyst",
      img: "https://images.pexels.com/photos/2287252/pexels-photo-2287252.jpeg",
      index: 9,
    },
  ];

  const [db, setDb] = useState(people);

  const handleDelete = (id) => {
    console.log("Delete clicked for index:", id);
    const forceUpdate = useRef(null);

    const newDB = db.filter((item) => item.index !== id);
    console.log("New DB after delete:", newDB);

    setDb(newDB);
    forceUpdate.current();
  };

  return (
    <div className="flex flex-wrap justify-center">
      {people.map((person) => {
        return (
          <Profile
            key={person.index}
            name={person.name}
            title={person.title}
            img={person.img}
            index={person.index}
            onDelete={handleDelete}
          />
        );
      })}
    </div>
  );
}

export default App;
