import React from "react";
import { Navbar, Form } from "react-bootstrap";
import styled from "styled-components";
import employees from "../../models/employees";

const StyledNav = styled(Navbar)`
  padding-left: 3rem;
  padding-right: 3rem;
  display: flex;
  background: white;
`;

const StyledForm = styled(Form.Control)`
  font-family:
    Varela Round,
    sans-serif;
  width: 18rem;
  margin-left: 5rem;
  display: flex;
  flex-wrap: wrap;
`;

function Search({ originalDb, setter }) {
  let timer;

  const handleChange = (e) => {
    clearTimeout(timer);

    timer = setTimeout(() => {
      const input = e.target.value;

      if (input === "") {
        employees
          .find({})
          .then((results) => {
            console.log(results);
            setter(results);
          })
          .catch((error) => {
            console.error(error);
          });
        return;
      }

      employees
        .find({
          $or: [
            { name: { $regex: input, $options: "i" } }, // Case-insensitive regex search for name
            { title: { $regex: input, $options: "i" } }, // Case-insensitive regex search for title
          ],
        })

        .then((results) => {
          console.log(results);
          setter(results);
        })
        .catch((error) => {
          console.error(error);
        });
    }, 300);
  };

  const disableEnter = (e) => {
    if (e.key === "Enter") {
      e.preventDefault();
    }
  };

  return (
    <StyledNav>
      <Form inline>
        <StyledForm
          type="text"
          placeholder="Search by Name or Title"
          onChange={handleChange}
          onKeyDown={disableEnter}
        />
      </Form>
    </StyledNav>
  );
}

export default Search;
