import React, { useState, useEffect } from "react";
import Profile from "../components/Profile";
import Add from "../components/Add";
import TopBar from "../components/TopBar";
import styled from "styled-components";
import axios from "axios";
import Missing from "../components/Missing";

//styling
const StyledDiv = styled.div`
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  padding-bottom: 1rem;
`;

function Home({ routes }) {
  //for showing the display
  const [db, setDb] = useState([]);

  const refreshDatabase = () => {
    axios
      .get("http://localhost:3001/api/employees")
      .then((response) => {
        setDb(response.data);
      })
      .catch((error) => console.error("Error:", error));
  };

  useEffect(() => {
    refreshDatabase();
  }, []);

  return (
    <>
      <StyledContainer>
        <Nav>
          <NavLink to={routes[0].path} exact className="nav-link">
            {routes[0].title}
          </NavLink>
          <NavLink to={routes[1].path} className="nav-link">
            {routes[1].title}
          </NavLink>
          <NavLink to={routes[2].path} className="nav-link">
            {routes[2].title}
          </NavLink>
          <NavLink to={routes[3].path} className="nav-link">
            {routes[3].title}
          </NavLink>
        </Nav>
      </StyledContainer>
      <TopBar
        routes={routes}
        setter={setDb}
        refreshDatabase={refreshDatabase}
      />
      <StyledDiv>
        {db.length !== 0 ? (
          db.map((person) => {
            return (
              <Profile
                key={person._id}
                person={person}
                refreshDatabase={refreshDatabase}
              />
            );
          })
        ) : (
          <Missing />
        )}
      </StyledDiv>
      <StyledDiv>
        <Add refreshDatabase={refreshDatabase} />
      </StyledDiv>
    </>
  );
}

export default Home;
