    { name: "Bob", hobby: "hiking" },
    { name: "Charlie", hobby: "basketball" },
    { name: "David", hobby: "singing" },
    { name: "Eva", hobby: "dancing" },
    { name: "Frank", hobby: "running" },
    { name: "Grace", hobby: "sleeping" },
    { name: "Henry", hobby: "trekking" },
    { name: "Irene", hobby: "rolling" },
    { name: "Jack", hobby: "typing" },
  ];

  const [isEditVisible, setIsEditVisible] = useState(false);
  const [isEditting, setIsEditting] = useState(false);

  const [name, setName] = useState("John Wick");
  const [bio, setBio] = useState("Programmer");

  const [newName, setNewName] = useState("");
  const [newBio, setNewBio] = useState("");

  const toggleEditVisibility = () => {
    setIsEditVisible(!isEditVisible);
  };

  const toggleEditting = () => {
    setIsEditting(!isEditting);
  };

  const handleNewName = (e) => {
    setNewName(e.target.value);
  };

  const handleNewBio = (e) => {
    setNewBio(e.target.value);
  };

  const handleChangeName = (e) => {
    setName(e.target.value);
  };

  const handleChangeBio = (e) => {
    setBio(e.target.value);
  };

  const handleClick = () => {
    if (newName.length != 0 && newBio.length != 0) {
      setName(newName);
      setBio(newBio);
      toggleEditVisibility();
      toggleEditting();
    }
  };

  return (
    <div
      class="max-w-sm bg-white border border-gray-200 rounded-lg shadow dark:bg-gray-800 dark:border-gray-700"
      style={{ display: "flex" }}
    >
      {people.map((people, index) => {
        return <Profile key={index} name={people.name} hobby={people.hobby} />;
      })}

