import React, { useState } from "react";
import { Navbar, Form, Button } from "react-bootstrap";

function Search({ database, setter }) {
  const [search, setSearch] = useState("");

  const original = database;

  const handleSearch = () => {
    if (search === "") {
      setter(original);
      return;
    }

    const filteredObjects = database.filter((obj) => obj.name.includes(search));
    setter(filteredObjects);
  };

  const handleChange = (e) => setSearch(e.target.value);

  console.log(search);

  return (
    <Navbar
      className="bg-body-tertiary"
      style={{
        paddingLeft: "3rem",
        paddingRight: "3rem",
        display: "flex",
      }}
    >
      <Form inline>
        <Form.Control
          style={{
            fontFamily: "Varela Round, sans-serif",
            width: "18rem",
            marginRight: "1rem",
          }}
          type="text"
          placeholder="Look for Employees by Name"
          className="mr-sm-2"
          onChange={handleChange}
        />
      </Form>
    </Navbar>
  );
}

export default Search;
