import React, { useState } from "react";
import {
  Nav,
  Navbar,
  Form,
  CloseButton,
  Button,
  Container,
  NavLink,
} from "react-bootstrap";
import styled from "styled-components";
import axios from "axios";

const StyledNav = styled(Navbar)`
  padding-left: 3rem;
  padding-right: 3rem;
  display: flex;
  background: white;
  justify-content: flex-end;
`;

const StyledForm = styled(Form.Control)`
  font-family:
    Varela Round,
    sans-serif;
  width: 16rem;
  margin-right: -2rem;
  display: flex;
  align-items: center;
`;

const StyledContainer = styled(Container)`
  margin-right: auto;
  margin-left: 0;
`;

const StyledCloseButton = styled(CloseButton)`
  margin-right: 1rem;
`;

const StyledButton = styled(Button)`
  margin-right: 0.5rem;
`;
const StyledLink = styled(NavLink)`
  font-size: 1rem;
  margin-right: 1rem;
  transition: border-radius ease;

  &:hover {
    background-color: #f0f0f0;
    border-radius: 20px;
  }

  &.active {
    text-decoration: underline;
  }
`;

function TopBar({ routes, setter, refreshDatabase }) {
  const [input, setInput] = useState("");

  const handleChange = (e) => {
    setInput(e.target.value);
  };

  const searchDatabase = (input) => {
    if (input === "") {
      refreshDatabase();
    } else {
      axios
        .get("http://localhost:3001/api/search/" + input)
        .then((response) => {
          console.log("search result : " + response.data);
          setter(response.data);
        })
        .catch((error) => console.error("Error:", error));
    }
  };

  const handleReset = () => {
    handleClear();
    refreshDatabase();
  };

  const handleClear = () => {
    setInput("");
  };

  const enterPressed = (e) => {
    if (e.key === "Enter") {
      searchDatabase(input);
    }
  };

  return (
    <StyledNav>
      {/* <StyledContainer>
        <Nav>
          <StyledLink to={routes[0].path} exact>
            {routes[0].title}
          </StyledLink>
          <StyledLink to={routes[1].path}>{routes[1].title}</StyledLink>
          <StyledLink to={routes[2].path}>{routes[2].title}</StyledLink>
          <StyledLink to={routes[3].path}>{routes[3].title}</StyledLink>
        </Nav>
      </StyledContainer> */}
      <Navbar bg="light" expand="lg">
        <Container>
          <Navbar.Brand href="/">My App</Navbar.Brand>
          <Navbar.Toggle aria-controls="basic-navbar-nav" />
          <Navbar.Collapse id="basic-navbar-nav">
            <Nav className="mr-auto">
              <NavLink to="/" exact className="nav-link">
                Home
              </NavLink>
              <NavLink to="/about" className="nav-link">
                About
              </NavLink>
              <NavLink to="/contact" className="nav-link">
                Contact
              </NavLink>
            </Nav>
          </Navbar.Collapse>
        </Container>
      </Navbar>

      <StyledForm
        type="text"
        placeholder="Search by Name or Title"
        value={input}
        onChange={handleChange}
        onKeyDown={enterPressed}
      />
      <StyledCloseButton onClick={handleClear} />
      <StyledButton
        onClick={() => {
          searchDatabase(input);
        }}
        variant="primary"
      >
        Search
      </StyledButton>
      <Button onClick={handleReset} variant="secondary">
        Reset
      </Button>
    </StyledNav>
  );
}

export default TopBar;
