import React, { useState, useEffect, useHistory } from "react";
import Profile from "../components/Profile";
import Add from "../components/Add";
import TopBar from "../components/TopBar";
import styled from "styled-components";
import axios from "axios";
import Missing from "../components/Missing";

//styling
const StyledDiv = styled.div`
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  padding-bottom: 1rem;
`;

function DirectoryPage({ routes, currRoute }) {
  const history = useHistory();

  useEffect(() => {
    const unlisten = history.listen(() => {
      // This code will be executed whenever the route changes
      console.log("Route changed!");
      searchDatabase(currRoute, "");
    });

    // Clean-up function
    return () => {
      // Stop listening to route changes when the component is unmounted
      unlisten();
    };
  }, [history]); // Dependency array includes history object to re-run the effect when it changes

  //for showing the display
  const [db, setDb] = useState([]);

  const searchDatabase = (currRoute, input) => {
    if (currRoute === "/") {
      axios

        .get("http://localhost:3001/api/employees")
        .then((response) => {
          setDb(response.data);
        })
        .catch((error) => console.error("Error:", error));
    } else {
      axios
        .get("http://localhost:3001/api/search" + currRoute + "/" + input)
        .then((response) => {
          console.log(
            "http://localhost:3001/api/search" + currRoute + "/" + input
          );
          console.log("search result : " + response.data);
          setDb(response.data);
        })
        .catch((error) => console.error("Error:", error));
    }
  };

  useEffect(() => {
    searchDatabase("/", "");
  }, []);

  return (
    <>
      <TopBar
        routes={routes}
        searchDatabase={searchDatabase}
        currRoute={currRoute}
      />
      <StyledDiv>
        {db.length !== 0 ? (
          db.map((person) => {
            return (
              <Profile
                key={person._id}
                person={person}
                searchDatabase={searchDatabase}
                currRoute={currRoute}
              />
            );
          })
        ) : (
          <Missing />
        )}
      </StyledDiv>
      <StyledDiv>
        <Add searchDatabase={searchDatabase} currRoute={currRoute} />
      </StyledDiv>
    </>
  );
}

export default DirectoryPage;
