import React from "react";
import { Navbar, Form } from "react-bootstrap";

function Search({ database, setter, resetter }) {
  let timer;

  const handleChange = (e) => {
    clearTimeout(timer);

    timer = setTimeout(() => {
      const input = e.target.value;

      if (input === "") {
        resetter();
        return;
      }

      const filteredArray = database.filter((obj) =>
        obj.name.toLowerCase().includes(input.toLowerCase())
      );

      setter(filteredArray);
    }, 300);
  };

  const disableEnter = (e) => {
    if (e.key === "Enter") {
      e.preventDefault();
    }
  };

  return (
    <Navbar
      className="bg-body-tertiary"
      style={{
        paddingLeft: "3rem",
        paddingRight: "3rem",
        display: "flex",
        width: "18rem",
        alignItems: "center",
        justifyContent: "center",
        padding: "1rem 2rem",
        margin: "0.5rem",
      }}
    >
      <Form inline>
        <Form.Control
          style={{
            fontFamily: "Varela Round, sans-serif",
            width: "18rem",
            marginLeft: "5rem",
          }}
          type="text"
          placeholder="Look for Employees by Name"
          className="mr-sm-2"
          onChange={handleChange}
          onKeyDown={disableEnter}
        />
      </Form>
    </Navbar>
  );
}

export default Search;
