import React, { useState } from "react";

function App() {
  const [isEditVisible, setIsEditVisible] = useState(false);

  const [name, setName] = useState("John Wick");
  const [bio, setBio] = useState("Programmer");

  const [newName, setNewName] = useState("");
  const [newBio, setNewBio] = useState("");

  const toggleEditVisibility = () => {
    setIsEditVisible(!isEditVisible);
  };

  const handleNewName = (e) => {
    setNewName(e.target.value);
  };

  const handleNewBio = (e) => {
    setNewBio(e.target.value);
  };

  const handleChangeName = (e) => {
    setName(e.target.value);
  };

  const handleChangeBio = (e) => {
    setBio(e.target.value);
  };

  const handleClick = () => {
    if (newName.length != 0 && newBio.length != 0) {
      setName(newName);
      setBio(newBio);
      toggleEditVisibility();
    }
  };

  return (
    <div class="max-w-sm bg-white border border-gray-200 rounded-lg shadow dark:bg-gray-800 dark:border-gray-700">
      <div class="p-5">
        <a href="#">
          <h5 class="mb-2 text-2xl font-bold tracking-tight text-gray-900 dark:text-white">
            {name}
          </h5>
        </a>
        <p class="mb-3 font-normal text-gray-700 dark:text-gray-400">{bio}</p>

        <button
          onClick={toggleEditVisibility}
          type="button"
          class="text-white bg-blue-700 hover:bg-blue-800 focus:ring-4 focus:ring-blue-300 font-medium rounded-lg text-sm px-5 py-2.5 mr-2 mb-2 dark:bg-blue-600 dark:hover:bg-blue-700 focus:outline-none dark:focus:ring-blue-800"
        >
          Edit
        </button>

        {isEditVisible && (
          <div style={{ color: "white" }}>
            <br></br>
            <p class="mb-3 font-normal text-gray-700 dark:text-gray-400">
              Change Info
            </p>
          </div>
        )}

        {isEditVisible && (
          <div>
            <input
              onChange={handleNewName}
              type="text"
              id="small-input"
              class="block w-full p-2 text-gray-900 border border-gray-300 rounded-lg bg-gray-50 sm:text-xs focus:ring-blue-500 focus:border-blue-500 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500"
            />
            <br></br>
          </div>
        )}
        {isEditVisible && (
          <div>
            <label
              for="small-input"
              class="block mb-2 text-sm font-medium text-gray-900 dark:text-white"
            >
              Name
            </label>

            <input
              onChange={handleNewBio}
              type="text"
              id="small-input"
              class="block w-full p-2 text-gray-900 border border-gray-300 rounded-lg bg-gray-50 sm:text-xs focus:ring-blue-500 focus:border-blue-500 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500"
            />
          </div>
        )}

        {isEditVisible && (
          <div>
            <br></br>
          </div>
        )}

        {isEditVisible && (
          <button
            onClick={handleClick}
            type="button"
            class="text-white bg-blue-700 hover:bg-blue-800 focus:ring-4 focus:ring-blue-300 font-medium rounded-lg text-sm px-5 py-2.5 mr-2 mb-2 dark:bg-blue-600 dark:hover:bg-blue-700 focus:outline-none dark:focus:ring-blue-800"
          >
            Change
          </button>
        )}
      </div>
    </div>
  );
}

export default App;
