import React, { useState } from "react";

function App() {
  const [name, setName] = useState("John Wick");
  const [bio, setBio] = useState("Programmer");

  const [newName, setNewName] = useState("");
  const [newBio, setNewBio] = useState("");

  const handleNewName = (e) => {
    setNewName(e.target.value);
  };

  const handleNewBio = (e) => {
    setNewBio(e.target.value);
  };

  const handleChangeName = (e) => {
    setName(e.target.value);
  };

  const handleChangeBio = (e) => {
    setBio(e.target.value);
  };

  const handleClick = () => {
    setName(newName);
    setBio(newBio);
  };

  return (
    <div class="max-w-sm bg-white border border-gray-200 rounded-lg shadow dark:bg-gray-800 dark:border-gray-700">
      <a href="#">
        <img class="rounded-t-lg" src="/docs/images/blog/image-1.jpg" alt="" />
      </a>
      <div class="p-5">
        <a href="#">
          <h5 class="mb-2 text-2xl font-bold tracking-tight text-gray-900 dark:text-white">
            {name}
          </h5>
        </a>
        <p class="mb-3 font-normal text-gray-700 dark:text-gray-400">{bio}</p>

        <input onChange={handleNewName} placeholder="Change Name"></input>
        <input onChange={handleNewBio} placeholder="Change Bio"></input>
        <br></br>
        <br></br>
        <button
          onClick={handleClick}
          type="button"
          class="text-white bg-blue-700 hover:bg-blue-800 focus:ring-4 focus:ring-blue-300 font-medium rounded-lg text-sm px-5 py-2.5 mr-2 mb-2 dark:bg-blue-600 dark:hover:bg-blue-700 focus:outline-none dark:focus:ring-blue-800"
        >
          Change
        </button>
      </div>
    </div>
  );
}

export default App;
